$scripts = @(
	{
		Remove-Item -LiteralPath @(
		  'C:\Windows\Panther\unattend.xml';
		  'C:\Windows\Panther\unattend-original.xml';
		  'C:\Windows\Setup\Scripts\Wifi.xml';
		) -Force -ErrorAction 'SilentlyContinue' -Verbose;
	};
	{
		$guestToolsLog = "C:\UTMGuestToolsInstall.log"
		try {
			$drives = Get-WmiObject Win32_LogicalDisk | Where-Object { $_.DriveType -in 2, 5 }
			$found = $false
			foreach ($drive in $drives) {
				$utmExe = Get-ChildItem -Path "$($drive.DeviceID)\" -Filter "utm-guest-tools-*.exe" -ErrorAction SilentlyContinue | Select-Object -First 1
				if ($utmExe) {
					Write-Output "Found UTM Guest Tools on $($drive.DeviceID)" | Out-File -Append $guestToolsLog
					Start-Process -FilePath $utmExe.FullName -ArgumentList "/S" -Wait -NoNewWindow
					Write-Output "UTM Guest Tools installation completed." | Out-File -Append $guestToolsLog
					$found = $true
					break
				}
			}
			if (-not $found) {
				Write-Output "UTM Guest Tools executable not found on any CD or USB drive." | Out-File -Append $guestToolsLog
			}
		} catch {
			Write-Output "Error during UTM Guest Tools installation: $_" | Out-File -Append $guestToolsLog
		}
	};
	{
        $logPath = "C:\EnableWinRM.log";
        Start-Transcript -Path $logPath -Append;

        try {
            # Set all network profiles to Private to avoid WinRM firewall exception errors
            Get-NetConnectionProfile | Where-Object {$_.NetworkCategory -eq 'Public'} | Set-NetConnectionProfile -NetworkCategory Private;
			# Enable WinRM
			winrm quickconfig -quiet;
			Start-Service WinRM;
            Set-Item -Path WSMan:\localhost\Service\AllowUnencrypted -Value $true;
            Set-Item -Path WSMan:\localhost\Service\Auth\Basic -Value $true;
            # This opens the firewall for WinRM HTTP (port 5986) on localhost
            Enable-PSRemoting -Force;
            # This opens the firewall for WinRM HTTP (port 5985) from any remote address
            Set-NetFirewallRule -Name 'WINRM-HTTP-In-TCP' -RemoteAddress Any;
        } catch {
            Write-Error "An error occurred while enabling WinRM: $_";
        }

        Stop-Transcript;
	};
	{
		Set-ItemProperty -LiteralPath 'Registry::HKLM\SOFTWARE\Microsoft\Windows NT\CurrentVersion\Winlogon' -Name 'AutoLogonCount' -Type 'DWord' -Force -Value 0;
	};
);

& {
  [float] $complete = 0;
  [float] $increment = 100 / $scripts.Count;
  foreach( $script in $scripts ) {
    Write-Progress -Activity 'Running scripts to finalize your Windows installation. Do not close this window.' -PercentComplete $complete;
    '*** Will now execute command «{0}».' -f $(
      $str = $script.ToString().Trim() -replace '\s+', ' ';
      $max = 100;
      if( $str.Length -le $max ) {
        $str;
      } else {
        $str.Substring( 0, $max - 1 ) + '…';
      }
    );
    $start = [datetime]::Now;
    & $script;
    '*** Finished executing command after {0:0} ms.' -f [datetime]::Now.Subtract( $start ).TotalMilliseconds;
    "`r`n" * 3;
    $complete += $increment;
  }
} *>&1 >> "C:\Windows\Setup\Scripts\FirstLogon.log";
